mapper.insert:
INSERT INTO MEMBER
(
        MEM_ID
    ,   ID
    ,   PROFILE_IMG
    ,   NICKNAME
    ,   JOB_CODE
    ,   PLATFORM_CODE
    ,   AGREE_YN
    ,   CREATE_ID
    ,   UPDATE_ID
 )
 VALUES
(
        #{memId}
    ,   #{id}
    ,   #{profileImg}
    ,   #{nickname}
    ,   #{jobCode}
    ,   #{platformCode}
    ,   #{agreeYn}
    ,   #{memId}
    ,   #{memId}
)

INSERT INTO DEKER_MEMBER
(
        MEM_ID
    ,   PASSWORD
)
VALUES (
        #{memId}
    ,   #{password}
)

INSERT INTO SOCIAL_MEMBER
(
        MEM_ID
    ,   SOCIAL_ID
)
VALUES
(
        #{memId}
    ,   #{socialId}
)

mapper.select:
SELECT ID id
FROM ACCT_MAIL_CHECK
WHERE ID = #{id}
AND CHECK_STRING = #{checkString}

SELECT      MEM.MEM_ID          memId
FROM MEMBER MEM
LEFT OUTER JOIN SOCIAL_MEMBER SM ON MEM.MEM_ID = SM.MEM_ID
WHERE   ID = #{id}
AND     PLATFORM_CODE = #{platformCode}
AND     SOCIAL_ID = #{socialId}
AND     MEM.LAST_DT > '9999-12-30'

mapper.insert:
INSERT INTO MEMBER_TAG
(
            MEM_TAG_ID
        ,   MEM_ID
        ,   CONTENTS
)
VALUES
(
            #{memTagId}
        ,   #{memId}
        ,   #{contents}
)

mapper.update:
UPDATE MEMBER
SET     JOB_CODE = #{jobCode}
    ,   PROFILE_IMG = #{profileImg}
WHERE MEM_ID = #{memId}
