mapper.select:
select COUNT(MKT_PRODUCT_ID)
from study_gh.MKT_PRODUCT p
where PRODUCT_CATEGORY = #{categoryId}

select MKT_RECENT_PRODUCT_ID AS mktRecentProductId
from  study_gh.MKT_RECENT_PRODUCT
where MEM_ID = #{memId} and PRODUCT_ID = #{productId}

mapper.update:
UPDATE  study_gh.MKT_RECENT_PRODUCT
SET     UPDATE_DT = sysdate()
WHERE   MKT_RECENT_PRODUCT_ID = #{mktRecentProductId}

mapper.select:
select PRODUCT_CATEGORY
from study_gh.MKT_PRODUCT p
where MKT_PRODUCT_ID = #{productId}

select count(*)
from study_gh.MKT_REVIEW r
where r.PRODUCT_ID = #{productId}

select MKT_PRODUCT_OPTION_ID AS productOptionId,
        PRODUCT_PRICE AS productPrice
from study_gh.MKT_PRODUCT_OPTION
where OPTION1 = #{option1} and OPTION1_DATA = #{option1Data} and PRODUCT_ID = #{mktProductId}


            and OPTION2 IS NULL and OPTION2_DATA IS NULL


            and OPTION2 = #{option2} and OPTION2_DATA= #{option2Data}

SELECT      COUNT(*)
FROM study_gh.MKT_ORDER MO
LEFT OUTER JOIN MKT_PRODUCT MP on MO.PRODUCT_ID = MP.MKT_PRODUCT_ID
LEFT OUTER JOIN study_gh.MKT_PRODUCT_OPTION MPO on MO.OPTION_ID = MPO.MKT_PRODUCT_OPTION_ID
WHERE   1=1
AND     MO.MEM_ID = #{memId}
AND     MO.CREATE_DT > #{period}
AND     MO.ORDER_STATE = #{deliveryState}

mapper.update:
UPDATE study_gh.MKT_ORDER
SET ORDER_STATE =#{level}
WHERE ORDER_ID = #{id}

UPDATE study_gh.MKT_ORDER
SET COMPLETED_DT =#{timeString}
WHERE ORDER_ID = #{id}

UPDATE study_gh.MKT_ORDER
SET ORDER_CONFIRM ='Y'
WHERE ORDER_ID = #{id}

UPDATE study_gh.MKT_REVIEW
SET 
        MY_STAR = #{myStar}
    ,

        PRO_REVIEW_IMG = #{proReviewImg}
    ,

        REVIEW_STRING = #{reviewString}

WHERE MKT_REVIEW_ID = #{mktReviewId}

mapper.insert:
INSERT INTO study_gh.MKT_ADDRESS
(
        ADD_ID
    ,   MEM_ID
    ,   ADD_NAME
    ,   PHONE_NUMBER
    ,   ADD_ZIP
    ,   ADDRESS
    ,   ADD_DETAIL
    ,   ADD_DEFAULT
    ,   ADD_NICKNAME
)
VALUES
(
        #{addId}
    ,   #{memId}
    ,   #{addName}
    ,   #{phoneNumber}
    ,   #{addZip}
    ,   #{address}
    ,   #{addDetail}
    ,   'Y'
    ,   #{addNickname}
)

mapper.update:
UPDATE study_gh.MKT_ADDRESS
SET ADD_DEFAULT = 'N'
WHERE ADD_ID = #{addId}

mapper.select:
SELECT COUNT(*)
FROM study_gh.MKT_ADDRESS
WHERE   MEM_ID = #{memId}
AND     ADD_ID = #{addId}
AND     ADD_DEFAULT = 'Y'

mapper.update:
UPDATE study_gh.MKT_ADDRESS
SET ADD_MAIN = 'N'
WHERE MEM_ID = #{memId}

UPDATE study_gh.MKT_ADDRESS
SET ADD_MAIN = 'Y'
WHERE MEM_ID = #{memId}
AND   ADD_ID = #{addId}

mapper.select:
select PRODUCT_PRICE AS productPrice
from  study_gh.MKT_PRODUCT_OPTION
where MKT_PRODUCT_OPTION_ID = #{productOptionId}
